const images=document.querySelectorAll(".image-cover"),options={root:null,rootMargin:"700px",threshold:.1};function handleImg(e,o){e.forEach(e=>{console.log(e.intersectionRatio),0<e.intersectionRatio&&loadImage(e.target)})}function loadImage(e){e.src=e.getAttribute("data")}const observer=new IntersectionObserver(handleImg,options);images.forEach(e=>{observer.observe(e)});